#' Factorial Designs
#'
#' Something here
#'
#' @param data the data as a data frame
#' @param aim The aim of the analysis: \code{n} (default) for sample size,
#'   \code{power} to estimate power
#' @param mode .
#' @param means a string naming the variable containing the means from
#'   \code{data}; the variable must be numeric.
#' @param sds a string naming the variable containing the standard deviations
#'   from \code{data}; the variable must be numeric.
#' @param factors a vector of strings naming the  factors from \code{data}.
#'   Not needed if \code{formula} is used.
#' @param within a vector of strings naming the repeated measures factors from
#'   \code{data}.
#' @param r Expected correlation among repeated measures
#' @param peta .
#' @param effect_type .
#' @param repeated_type .
#' @param df_effect .
#' @param effect_groups .
#' @param design_groups .
#' @param power Minimal desired power
#' @param n Sample size
#' @param sig.level Type I error rate (significance cut-off or alpha)
#' @param plot_contour .
#' @param plot_escurve .
#' @param plot_ncurve .
#' @param plot_log .
#' @param plot_palette .
#' @param plot_x .
#' @param plot_y .
#' @param plot_custom_labels .
#' @param plot_z .
#' @param plot_x_from .
#' @param plot_x_to .
#' @param plot_z_lines .
#' @param plot_z_value .
#' @param plot_to_table .
#' @param ncp_type .
#' @param emeans .
#' @param esos .
#' @param .interface Used for internal purposes
#' @param .caller .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$intro} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$extrainfo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$issues} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$powertab} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$powerbyes} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$means} \tab \tab \tab \tab \tab an array of predicted means tables \cr
#'   \code{results$plotnotes} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$powerContour} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$powerEscurve} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$powerNcurve} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$powerCustom} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$customnotes} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$customtable} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$powertab$asDF}
#'
#' \code{as.data.frame(results$powertab)}
#'
#' @export
pamlfactorial <- function(
    data,
    aim = "n",
    mode = "facpeta",
    means = NULL,
    sds = NULL,
    factors = NULL,
    within = NULL,
    r = 0,
    peta = 0.2,
    effect_type,
    repeated_type,
    df_effect = 1,
    effect_groups = 2,
    design_groups = 2,
    power = 0.9,
    n = 20,
    sig.level = 0.05,
    plot_contour = FALSE,
    plot_escurve = FALSE,
    plot_ncurve = FALSE,
    plot_log = FALSE,
    plot_palette = "viridis",
    plot_x = "none",
    plot_y = "none",
    plot_custom_labels = FALSE,
    plot_z = "none",
    plot_x_from = 0,
    plot_x_to = 0,
    plot_z_lines = 0,
    plot_z_value = list(),
    plot_to_table = FALSE,
    ncp_type,
    emeans = FALSE,
    esos = FALSE,
    .interface = "jamovi",
    .caller = "factorial") {
    if (!requireNamespace("jmvcore", quietly = TRUE)) {
        stop("pamlfactorial requires jmvcore to be installed (restart may be required)")
    }

    if (!missing(means)) means <- jmvcore::resolveQuo(jmvcore::enquo(means))
    if (!missing(sds)) sds <- jmvcore::resolveQuo(jmvcore::enquo(sds))
    if (!missing(factors)) factors <- jmvcore::resolveQuo(jmvcore::enquo(factors))
    if (!missing(within)) within <- jmvcore::resolveQuo(jmvcore::enquo(within))
    if (missing(data)) {
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`(!missing(means), means, NULL),
            `if`(!missing(sds), sds, NULL),
            `if`(!missing(factors), factors, NULL),
            `if`(!missing(within), within, NULL)
        )
    }

    for (v in factors) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- pamlfactorialOptions$new(
        aim = aim,
        mode = mode,
        means = means,
        sds = sds,
        factors = factors,
        within = within,
        r = r,
        peta = peta,
        effect_type = effect_type,
        repeated_type = repeated_type,
        df_effect = df_effect,
        effect_groups = effect_groups,
        design_groups = design_groups,
        power = power,
        n = n,
        sig.level = sig.level,
        plot_contour = plot_contour,
        plot_escurve = plot_escurve,
        plot_ncurve = plot_ncurve,
        plot_log = plot_log,
        plot_palette = plot_palette,
        plot_x = plot_x,
        plot_y = plot_y,
        plot_custom_labels = plot_custom_labels,
        plot_z = plot_z,
        plot_x_from = plot_x_from,
        plot_x_to = plot_x_to,
        plot_z_lines = plot_z_lines,
        plot_z_value = plot_z_value,
        plot_to_table = plot_to_table,
        ncp_type = ncp_type,
        emeans = emeans,
        esos = esos,
        .interface = .interface,
        .caller = .caller
    )

    analysis <- pamlfactorialClass$new(
        options = options,
        data = data
    )

    analysis$run()

    analysis$results
}
