pamlpropBase <- if (requireNamespace("jmvcore", quietly = TRUE)) {
    R6::R6Class(
        "pamlpropBase",
        inherit = jmvcore::Analysis,
        public = list(
            initialize = function(options, data = NULL, datasetId = "", analysisId = "", revision = 0) {
                super$initialize(
                    package = "pamlj",
                    name = "pamlprop",
                    version = c(0, 0, 1),
                    options = options,
                    results = pamlpropResults$new(options = options),
                    data = data,
                    datasetId = datasetId,
                    analysisId = analysisId,
                    revision = revision,
                    pause = NULL,
                    completeWhenFilled = FALSE,
                    requiresMissings = FALSE,
                    weightsSupport = "na"
                )
            }
        )
    )
}

#' Proportions
#'
#' Something here
#'
#' @param aim .
#' @param mode .
#' @param propind_p1 .
#' @param propind_p2 .
#' @param propind_n .
#' @param propind_nratio .
#' @param propone_p1 .
#' @param propone_p2 .
#' @param propone_n .
#' @param proppaired_p1 .
#' @param proppaired_p2 .
#' @param proppaired_n .
#' @param power .
#' @param sig.level .
#' @param alternative .
#' @param plot_contour .
#' @param plot_escurve .
#' @param plot_ncurve .
#' @param plot_log .
#' @param plot_palette .
#' @param plot_x .
#' @param plot_y .
#' @param plot_custom_labels .
#' @param plot_z .
#' @param plot_x_from .
#' @param plot_x_to .
#' @param plot_z_lines .
#' @param plot_z_value .
#' @param plot_to_table .
#' @param es_type .
#' @param explain add an additional description of the analysis carried out
#' @param .interface Used for internal purposes
#' @param .caller .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$intro} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$issues} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$extrainfo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$powertab} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$powerbyes} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plotnotes} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$powerContour} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$powerEscurve} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$powerNcurve} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$powerCustom} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$customnotes} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$customtable} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$powertab$asDF}
#'
#' \code{as.data.frame(results$powertab)}
#'
#' @export
pamlprop <- function(
    aim = "n",
    mode = "propind",
    propind_p1 = 0.6,
    propind_p2 = 0.5,
    propind_n = 20,
    propind_nratio = 1,
    propone_p1 = 0.6,
    propone_p2 = 0.5,
    propone_n = 50,
    proppaired_p1 = 0.1,
    proppaired_p2 = 0.4,
    proppaired_n = 20,
    power = 0.9,
    sig.level = 0.05,
    alternative = "two.sided",
    plot_contour = FALSE,
    plot_escurve = FALSE,
    plot_ncurve = FALSE,
    plot_log = TRUE,
    plot_palette = "viridis",
    plot_x = "none",
    plot_y = "none",
    plot_custom_labels = FALSE,
    plot_z = "none",
    plot_x_from = 0,
    plot_x_to = 0,
    plot_z_lines = 0,
    plot_z_value = list(),
    plot_to_table = FALSE,
    es_type = "odd",
    explain = FALSE,
    .interface = "jamovi",
    .caller = "proportions") {
    if (!requireNamespace("jmvcore", quietly = TRUE)) {
        stop("pamlprop requires jmvcore to be installed (restart may be required)")
    }


    options <- pamlpropOptions$new(
        aim = aim,
        mode = mode,
        propind_p1 = propind_p1,
        propind_p2 = propind_p2,
        propind_n = propind_n,
        propind_nratio = propind_nratio,
        propone_p1 = propone_p1,
        propone_p2 = propone_p2,
        propone_n = propone_n,
        proppaired_p1 = proppaired_p1,
        proppaired_p2 = proppaired_p2,
        proppaired_n = proppaired_n,
        power = power,
        sig.level = sig.level,
        alternative = alternative,
        plot_contour = plot_contour,
        plot_escurve = plot_escurve,
        plot_ncurve = plot_ncurve,
        plot_log = plot_log,
        plot_palette = plot_palette,
        plot_x = plot_x,
        plot_y = plot_y,
        plot_custom_labels = plot_custom_labels,
        plot_z = plot_z,
        plot_x_from = plot_x_from,
        plot_x_to = plot_x_to,
        plot_z_lines = plot_z_lines,
        plot_z_value = plot_z_value,
        plot_to_table = plot_to_table,
        es_type = es_type,
        explain = explain,
        .interface = .interface,
        .caller = .caller
    )

    analysis <- pamlpropClass$new(
        options = options,
        data = data
    )

    analysis$run()

    analysis$results
}
